import { Formik, Form} from "formik";
import FormFields from "../components/FormFields";
import * as Yup from "yup"
import { gql, useMutation } from "@apollo/client";

const CREATE_TASK = gql`
  mutation CreateTask($name: String!, $description: String!, $date: String!, $urgent: Boolean!) {
    addTask(name: $name, description: $description, date: $date, urgent: $urgent) {
      success
      message
    } 
  }
`

const NewTask = ({refetchTasks}) => {
  const [createTask, {data}] = useMutation(CREATE_TASK, {
    onCompleted: () => refetchTasks()
  })

  return (
    <div className="bg-origin-padding w-min rounded-lg bg-gray-300 bg-opacity-90 shadow-2xl mt-10 ml-10 p-4">
      <Formik 
      initialValues={{name: "", description: "", date: "", urgent: false}}
      validationSchema={Yup.object({
        name: Yup.string().required("Required"),
        description: Yup.string().required("Required"),
        date: Yup.date(),
        urgent: Yup.bool()
      })}
      onSubmit={(values, { setSubmitting }) => {
        setTimeout(() => {
          createTask({variables: {
            name: values.name,
            description: values.description,
            date: new Date(values.date).toISOString(),
            urgent: values.urgent
          }})
          setSubmitting(false);
        }, 400);
      }}
      className="flex items-center justify-center">
        <Form>
          <FormFields label="Title" name="name" type="text"/>
          <FormFields label="Description" name="description" type="textarea"/>
          <FormFields label="Date" name="date" type="date" />
          <FormFields label="Urgent" name="urgent" type="checkbox" />
          <button type="submit" className="rounded w-full bg-green-200 hover:bg-green-500">Create</button>
        </Form>
      </Formik>
      {data ? data.addTask.message : null}
    </div>
  );
};

export default NewTask;
