import DayCell from "./DayView";


const Calendar = ({ month }) => {
  const fillMonthDays = (days) => {
    let dayArray = [];
    for (let day = 1; day < days; day++) {
      dayArray.push(day);
    }
    return dayArray;
  };
  const fillMonthWeeks = (days) => {
    let weekArray = [];
    for (let week = 0; week < Math.ceil(days / 7); week++) {
      weekArray.push(week);
    }
    return weekArray;
  };
  const daysInMonth = {
    january: 31,
    febuary: 28,
    march: 31,
    april: 30,
    may: 31,
    june: 30,
    july: 31,
    august: 31,
    september: 30,
    october: 31,
    november: 30,
    december: 31,
  };
  const date = {
    months: {
      january: {
        days: fillMonthDays(daysInMonth.january),
        weeks: fillMonthWeeks(daysInMonth.january),
      },
      febuary: {
        days: fillMonthDays(daysInMonth.febuary),
        weeks: fillMonthWeeks(daysInMonth.febuary),
      },
      march: {
        days: fillMonthDays(daysInMonth.march),
        weeks: fillMonthWeeks(daysInMonth.march),
      },
      april: {
        days: fillMonthDays(daysInMonth.april),
        weeks: fillMonthWeeks(daysInMonth.april),
      },
      may: {
        days: fillMonthDays(daysInMonth.may),
        weeks: fillMonthWeeks(daysInMonth.may),
      },
      june: {
        days: fillMonthDays(daysInMonth.june),
        weeks: fillMonthWeeks(daysInMonth.june),
      },
      july: {
        days: fillMonthDays(daysInMonth.july),
        weeks: fillMonthWeeks(daysInMonth.july),
      },
      august: {
        days: fillMonthDays(daysInMonth.august),
        weeks: fillMonthWeeks(daysInMonth.august),
      },
      september: {
        days: fillMonthDays(daysInMonth.september),
        weeks: fillMonthWeeks(daysInMonth.september),
      },
      october: {
        days: fillMonthDays(daysInMonth.october),
        weeks: fillMonthWeeks(daysInMonth.october),
      },
      november: {
        days: fillMonthDays(daysInMonth.november),
        weeks: fillMonthWeeks(daysInMonth.november),
      },
      december: {
        days: fillMonthDays(daysInMonth.december),
        weeks: fillMonthWeeks(daysInMonth.december),
      },
    },
  };
  return (
    <div>
      {date.months[month].weeks.map((week) => {
        return (
          <tr key={week}>
            {date.months[month].days
              .slice(week * 7, week * 7 + 7)
              .map((day) => {
                return (
                  <td key={day}>
                    <DayCell day={day} />
                  </td>
                );
              })}
          </tr>
        );
      })}
    </div>
  );
};

export default Calendar;
