const TaskView = ({ task }) => {
  return (
    <div
      className={`rounded-lg shadow-2xl flex flex-col ${
        task.urgent ? "bg-red-400" : "bg-blue-400"
      }`}
    >
      <div className="group flex flex-row">
        <div className="font-bold text-xl ml-auto">
          {task.name}
        </div>
        <div className=" ml-auto mr-2 group">
        <button className="group-hover:bg-red-800 bg-yellow-300 rounded-full">X</button></div>
      </div>
      <div className="font-bold">Description</div>
      <div className="bg-yellow-300 rounded">{task.description}</div>
      <div>{new Date(Number(task.date)).toString()}</div>
      <div>Manager: {task.manager.name}</div>
      <div>
        Assigned to: {task.assignees.map((assignee) => `${assignee.name},`)}
      </div>
    </div>
  );
};

export default TaskView;
