import { Field, ErrorMessage } from "formik";

const FormFields = ({ label, name, type }) => {
  return (
    <div>
      <label htmlFor={name} className="font-bold text-indigo-600">
        {label}
      </label>
      <Field name={name} type={type} className="rounded focus:outline-none focus:ring"/>
      <ErrorMessage
        name={name}
        component="div"
        className="text-red-600 text-sm"
      />
    </div>
  );
};

export default FormFields;
