import { gql, useMutation } from "@apollo/client";

const DELETE_TASK = gql`
  mutation DeleteTask($id: Int!){
      deleteTask(id: $id) {
        message
        success
      }
  }
`

const TaskView = ({ task }) => {
  const [deleteTask] = useMutation(DELETE_TASK)

  return (
    <div
      className={`rounded-lg shadow-2xl flex flex-col ${
        task.urgent ? "bg-red-400" : "bg-blue-400"
      }`}
    >
      <div className="flex flex-row">
        <div className="font-bold text-xl ml-auto">{task.name}</div>
        <div className="group relative inline-block ml-auto mr-2 mt-1">
          <button onClick={() => deleteTask({variables: {id: task.id}})} className="hover:bg-red-600 bg-green-600 rounded-full w-6 ">
            X
          </button>
          <span className="group-hover:inline-block absolute bg-gray-600 rounded shadow-lg z-50 left-1/2 top-full -ml-8 text-center text-white hidden">
            Delete
          </span>
        </div>
      </div>
      <div className="font-bold">Description</div>
      <div className="bg-yellow-300 rounded">{task.description}</div>
      <div>{new Date(Number(task.date)).toString()}</div>
      <div>Manager: {task.manager.name}</div>
      <div>
        Assigned to: {task.assignees.map((assignee) => `${assignee.name},`)}
      </div>
    </div>
  );
};

export default TaskView;
