import { gql, useQuery } from "@apollo/client";
import { BrowserRouter as Router, Redirect, Route } from "react-router-dom";
import AppView from "./views/AppView";
import LoginView from "./views/LoginView";
import SignUpView from "./views/SignUpView";

const GET_USER = gql`
  query GetUser {
    getUser {
      id
      name
      email
    }
  }
`;

function App() {
  const { loading, error, data, refetch} = useQuery(GET_USER);
  if (loading) return "Logging in...";
  return (
    <Router>
      <Route path="/" exact>
        {data.getUser ? <AppView/> : <Redirect to="/login" />}
      </Route>
      <Route path="/login">
        {data.getUser ? <Redirect to="/" /> : <LoginView refetchUsers={refetch} />}
      </Route>
      <Route path="/signup">
        {data.getUser ? <Redirect to="/" /> : <SignUpView refetchUsers={refetch} />}
      </Route>
    </Router>
  );
}

export default App;
