const TaskView = ({task}) => {
    return (
        <div className={`rounded-lg shadow-2xl flex flex-col justify-center items-center ${task.urgent ? "bg-red-400" : "bg-blue-400"}`}>
            <div className="font-bold text-xl">{task.name}</div>
            <div className="font-bold">Description</div>
            <span className="bg-yellow-300 rounded">{task.description}</span> 
            <div>{new Date(Number(task.date)).toString()}</div>
            <div>Manager: {task.manager.name}</div>
            <div>Assigned to: {task.assignees.map(assignee => `${assignee.name},`)}</div>

        </div>
    )
}

export default TaskView
