import { Form, Formik } from "formik";
import FormFields from "../components/FormFields";
import * as Yup from 'yup'

export const ConnectionsView = () => {
  return (
    <div className="bg-origin-padding w-min h-auto rounded-lg bg-gray-300 bg-opacity-90 shadow-2xl mt-10 ml-10 p-4" >
      <Formik
        initialValues={{ email: "" }}
        validationSchema={Yup.object({
          email: Yup.string()
            .email("Invalid email address")
            .required("Required")
        })}
        onSubmit={(values, { setSubmitting }) => {
          setTimeout(() => {
            setSubmitting(false);
          }, 400);
        }}
        className="flex items-center justify-center"
      >
        <Form>
          <FormFields label="User's Email" name="email" type="email" />
          <button type="submit" className="rounded w-full bg-green-200 hover:bg-green-500">Connect</button>
        </Form>
      </Formik>
    </div>
  );
};
