const TaskView = ({ task }) => {
  return (
    <div
      className={`rounded-lg shadow-2xl flex flex-col ${
        task.urgent ? "bg-red-400" : "bg-blue-400"
      }`}
    >
      <div className="flex flex-row">
        <div className="font-bold text-xl ml-auto">{task.name}</div>
        <div className="group relative ml-auto mr-2">
          <button className="hover:bg-red-600 bg-green-600 rounded-full w-6 mt-1">
            X
          </button>
          <div className="group-hover:flex  relative bg-gray-600 rounded shadow-lg z-50 text-white hidden">
            Delete
          </div>
        </div>
      </div>
      <div className="font-bold">Description</div>
      <div className="bg-yellow-300 rounded">{task.description}</div>
      <div>{new Date(Number(task.date)).toString()}</div>
      <div>Manager: {task.manager.name}</div>
      <div>
        Assigned to: {task.assignees.map((assignee) => `${assignee.name},`)}
      </div>
    </div>
  );
};

export default TaskView;
